DROP TABLE IF EXISTS `item_stats_models`;

CREATE TABLE `item_stats_models` (
  `id` int unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL,
  `x` varchar(255) NOT NULL,
  `y` varchar(255) NOT NULL,
  `intercept` float NOT NULL,
  `rank1` float NOT NULL,
  `rank2` float NOT NULL,
  `item_class` int NOT NULL,
  `item_class_name` varchar(255) NOT NULL,
  `item_subclass` varchar(255) NOT NULL,
  `item_subclass_names` varchar(255) NOT NULL,
  `item_inventory_type` int NOT NULL,
  `item_inventory_type_name` varchar(255) NOT NULL,
  `ItemLevels_start` int NOT NULL,
  `ItemLevels_end` int NOT NULL,
  `model_desc` text NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=73 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

