REPLACE INTO quest_offer_reward_locale (`ID`,`locale`,`RewardText`,`VerifiedBuild`) VALUES (26109,'zhCN','你再次证明了你的价值，冒险者。有了这些徽记，你现在将学习北极飞行技巧，赋予你在冰封苔原上掌控天空的自由。与觉醒者的战斗仍在继续，但有了这一技能，你距离胜利又近了一步。',0);
